import React, { useState } from "react";
import { StatusBar } from "expo-status-bar";
import { StyleSheet, Text, View, TouchableOpacity, Image } from "react-native";
import { logOut } from "../firebase/fb.methods";

export default function Dashboard({ navigation }) {
  let currUserId = firebase.auth().currentUser.uid;
  const [firstName, setFirstName] = useState("");

  useEffect(() => {
    async function getUserInfo() {
      let doc = await firebase
        .firestore()
        .collection("users")
        .doc(currUserId)
        .get();

      if (!doc.exists) {
        alert("Not found!");
      } else {
        let dataObj = doc.data();
        setFirstName(dataObj.firstName);
      }
    }
    getUserInfo();
  });
  const onPress = () => {
    logOut();
    navigation.replace("Home");
  };

  return (
    <View style={styles.container}>
      <Text style={styles.textTitle}>Dashboard</Text>
      <Text style={styles.text}>Hello, {firstName}</Text>
      <TouchableOpacity style={styles.btn} onPress={onPress}>
        <Text style={styles.buttonText}>Log Out</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#B8D1A9",
    alignItems: "center",
    justifyContent: "center",
  },
  buttonText: {
    marginTop: 40,
    fontSize: 36,
    fontWeight: "bold",
    letterSpacing: 0.25,
    color: "black",
  },
  textTitle: {
    fontSize: 30,
    color: "black",
    textAlign: "center",
  },
  btn: {
    width: "80%",
    borderRadius: 25,
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    marginTop: 40,
    backgroundColor: "#FFCC24",
  },
  textInput: {
    height: 48,
    borderRadius: 5,
    overflow: "hidden",
    backgroundColor: "white",
    marginTop: 10,
    marginBottom: 10,
    marginLeft: 30,
    marginRight: 30,
    paddingLeft: 16,
  },
  text: {
    fontSize: 16,
    fontWeight: "bold",
    letterSpacing: 0.25,
    color: "black",
  },
});
